import UIKit

class ViewController: UIViewController {
    
    private let FOLDER_NAME = "folder"
    @IBOutlet weak var availableSpaceLabel: UILabel!
    @IBOutlet weak var totalSpaceLabel: UILabel!
    
    private var isCreatingFiles = false
    private var applicationSupportDirectory: String!
    
    override func viewDidLoad() {
        super.viewDidLoad()
        
        let fileManager = NSFileManager.defaultManager()
        
        var paths = NSSearchPathForDirectoriesInDomains(NSSearchPathDirectory.ApplicationSupportDirectory, NSSearchPathDomainMask.LocalDomainMask, true)
        var applicationSupportDirPath = paths.first! as String
        applicationSupportDirectory = "\(NSHomeDirectory())\(applicationSupportDirPath)/\(self.FOLDER_NAME)"
        

        var isDirectory: ObjCBool = true
        
        if (fileManager.fileExistsAtPath(applicationSupportDirectory, isDirectory: &isDirectory)) {
            println("Directory exists: \(applicationSupportDirectory)")
        } else {
            var error: NSError?
            fileManager.createDirectoryAtPath(applicationSupportDirectory, withIntermediateDirectories: true, attributes: nil, error: &error)
            if (error != nil) {
                println("Error creating folder dir: \(error)")
            } else {
                println("Directory '\(applicationSupportDirectory)' created!")
            }
        }

        
        self.updateStorageInfoLabels()
    }
    
    @IBAction func fillDeviceStorageButtonTapped(sender: AnyObject) {
        if (isCreatingFiles) {
            isCreatingFiles = false
        } else {
            isCreatingFiles = true
            ActivityIndicatorHelper.showActivityIndicatorAtView(self.view)
            dispatch_async(dispatch_queue_create("create files queue", nil), { () -> Void in
                let fileManager = NSFileManager.defaultManager()
//                let documentsDirectory = NSHomeDirectory().stringByAppendingPathComponent(self.FOLDER_NAME)
                var error: NSError?
                
                var index = 0
                while((error == nil) && self.isCreatingFiles) {
                    let filePath = self.applicationSupportDirectory.stringByAppendingPathComponent("file\(index++).txt")
//                    var text = "iPhone test test test test test test"
                    
                    self.textToSave.writeToFile(filePath, atomically: true, encoding: NSUTF8StringEncoding, error: &error)
                    
                    if (index % 1000 == 0) {
                        dispatch_async(dispatch_get_main_queue(), { () -> Void in
                            self.updateStorageInfoLabels()
                        })
                    }
                }
                
                if (error != nil) {
                    println("\nError removing folder: \(error)")
                }
                
                ActivityIndicatorHelper.hideActivityIndicatorAtView(self.view)
            })
        }
    }
    
    @IBAction func removeCreatedFilesButtonTapped(sender: AnyObject) {
        ActivityIndicatorHelper.showActivityIndicatorAtView(self.view)
        dispatch_async(dispatch_queue_create("remove folder queue", nil), { () -> Void in
            let fileManager = NSFileManager.defaultManager()
//            let documentsDirectory = NSHomeDirectory().stringByAppendingPathComponent(self.FOLDER_NAME)
            var error: NSError?
            
            fileManager.removeItemAtPath(self.applicationSupportDirectory, error: &error)
            
            if (error != nil) {
                println("\nError removing folder: \(error)")
            }
            ActivityIndicatorHelper.hideActivityIndicatorAtView(self.view)
        })
    }
    
    private func updateStorageInfoLabels() {
        var result = getSpaceAvailable()
        
        totalSpaceLabel.text = "\(result.totalSpace/(1024*1024))mb"
        availableSpaceLabel.text = "\(result.freeSpace/(1024*1024))mb"
    }
    
