    private func getSpaceAvailable() -> (totalSpace: UInt64, freeSpace: UInt64) {
        var totalSpace: UInt64 = 0
        var totalFreeSpace: UInt64 = 0
        
        var error: NSError?
        var paths = NSSearchPathForDirectoriesInDomains(NSSearchPathDirectory.DocumentDirectory, NSSearchPathDomainMask.UserDomainMask, true)
        var dictionary: Dictionary = NSFileManager.defaultManager().attributesOfFileSystemForPath(paths.last as String!, error: &error)!
        
        var fileSystemSizeInBytes = dictionary[NSFileSystemSize]
        var freeFileSystemSizeInBytes = dictionary[NSFileSystemFreeSize]
        
        totalSpace = fileSystemSizeInBytes!.unsignedLongLongValue
        totalFreeSpace = freeFileSystemSizeInBytes!.unsignedLongLongValue
        
        return (totalSpace, totalFreeSpace)
    }
    
    
